import React, { useState } from "react";
import TextField from '@material-ui/core/TextField';
import {Button, CardContent } from "@material-ui/core";
import axios from "axios";
import Card from "@material-ui/core/Card";
import { makeStyles } from '@material-ui/core/styles';
import searchGlass from "./pngs/icons8-search-64.png";

function UserInfo() {

    const [userInfo, setUserInfo] = useState({ username: "", locationPref: "", success: false, error: "" });

    async function saveprefs() {
        const config = {
            headers: {
                "Content-Type": "application/json",
                "Access-Control-Allow-Origin": "*"
            }
        }
        var body = {
            username: userInfo.username,
            locationPref: userInfo.locationPref
        }

        await axios.post("/users", body, config)
            .then(res => {
                setUserInfo({ ...userInfo, success: res.success })
                setUserInfo({ ...userInfo, error: res.error })
            })

    }

    function handleUsernameChange(input) {
        setUserInfo({ ...userInfo, username: input })
    }

    function handleLocationPrefChange(input) {
        setUserInfo({ ...userInfo, locationPref: input })
    }

    const useStyles = makeStyles({
        card: {

            display: "",
            minWidth: "10px",
            minHeight: "10px",
            maxWidth: "400px",
            margin: "50px",

        },
        textfield: {
            margin: "10px",

        },

        SignInForm: {
            margin: "12px",
        }

    });
    const classes = useStyles()



    return (
        <div>
            <Card className={classes.card}>
                <CardContent>
                    <form className={classes.SignInForm} noValidate autoComplete="off">
                        <TextField id="standard-basic" label="Username"
                            onChange={(event) => handleUsernameChange(event.target.value)}
                        />


                        <TextField id="standard-basic" label="Home City"
                            onChange={(event) => handleLocationPrefChange(event.target.value)}
                        />


                    </form>
                    <Button variant="contained" color="primary"
                        onClick={() => { saveprefs() }}
                    >
                        Save Preferences
        </Button>
                </CardContent>
            </Card>
        </div>
    )

}

export default UserInfo;
